// Validar e preparar dados
const siteSlug = $input.first().json.params?.siteSlug || $input.first().json.body?.siteSlug;
const body = $input.first().json.body || {};

if (!siteSlug) {
  return {
    json: {
      success: false,
      error: 'siteSlug Ã© obrigatÃ³rio'
    },
    statusCode: 400
  };
}

// Preparar dados para atualizaÃ§Ã£o (mapear do formato frontend para banco)
const updates = {
  show_brand: body.showBrand !== undefined ? body.showBrand : null,
  show_phone: body.showPhone !== undefined ? body.showPhone : null,
  show_whatsapp: body.showWhatsApp !== undefined ? body.showWhatsApp : null,
  whatsapp_number: body.whatsAppNumber || null,
  footer_text: body.footerText || null,
  color_scheme: body.colorScheme || null,
  theme: body.theme ? JSON.stringify(body.theme) : null,
  custom_css: body.customCSS || null,
  primary_color: body.primaryColor || null,
  background_color: body.backgroundColor || null,
  accent_color: body.accentColor || null,
  text_color: body.textColor || null,
  shadow_color: body.shadowColor || null,
  border_color: body.borderColor || null
};

// Construir SQL dinamicamente com valores escapados
const values = [
  siteSlug,
  updates.show_brand,
  updates.show_phone,
  updates.show_whatsapp,
  updates.whatsapp_number,
  updates.footer_text,
  updates.color_scheme,
  updates.theme,
  updates.custom_css,
  updates.primary_color,
  updates.background_color,
  updates.accent_color,
  updates.text_color,
  updates.shadow_color,
  updates.border_color
];

return {
  json: {
    siteSlug: siteSlug,
    updates: updates,
    sqlValues: values
  }
};

// Formatar resposta
const dbRow = $input.first().json;

const settings = {
  siteSlug: dbRow.site_slug,
  showBrand: dbRow.show_brand ?? true,
  showPhone: dbRow.show_phone ?? false,
  showWhatsApp: dbRow.show_whatsapp ?? false,
  whatsAppNumber: dbRow.whatsapp_number || null,
  footerText: dbRow.footer_text || null,
  colorScheme: dbRow.color_scheme || null,
  theme: dbRow.theme || {},
  customCSS: dbRow.custom_css || null,
  primaryColor: dbRow.primary_color || null,
  backgroundColor: dbRow.background_color || null,
  accentColor: dbRow.accent_color || null,
  textColor: dbRow.text_color || null,
  shadowColor: dbRow.shadow_color || null,
  borderColor: dbRow.border_color || null,
  lastUpdated: dbRow.updated_at || dbRow.created_at
};

return {
  json: {
    success: true,
    settings: settings,
    message: 'ConfiguraÃ§Ãµes atualizadas com sucesso'
  }
};
